    res.status(200).send('HAPPY');
});

// Another public directory
web.use('/another/', express.static(__dirname + '/public2'));

// Another Normal Route
web.get('/good2', function(req, res) {
    res.status(200).send('MORE HAPPY');
});

// Custom 404 Catcher
// DELETE THIS, AND /another/* WORKS, KEEP IT AND /another/* DOES NOT WORK
