    this.message = (message == null ? '' : (message + ''))
      .slice(0, maxMessageLength);
    if (_.isFunction(ErrorConstructor.captureStackTrace)) {
      ErrorConstructor.captureStackTrace(
        this,
        _.isFunction(stackStartFn) ? stackStartFn : this.constructor
      );
    } else {
      var err = ErrorConstructor.call(this);
      this.stack = '';
      if (typeof err.stack === 'string') {
        this.stack = err.stack;
      } else if (typeof err.stacktrace === 'string') {
        this.stack = err.stacktrace;
      }
