     * This method calculates the Power Set of the array. Sparseness is ignored.
     *
     * Note: Function is recursive (susceptible to call stack limits).
     *
     * @static
     * @memberOf _
     * @category Math
     * @param {Array} array The array to create a power set from.
     * @returns {Array} Returns the power set as a new array.
     * @see http://en.wikipedia.org/wiki/Power_set
     * @example
     *
     * var set = [0, 1, 2];
     *
     * _-powerSet(set);
     * // => [[], [3], [2] , [2, 3] , [1], [1, 3], [1, 2], [1, 2, 3]]
     */
    function powerSet(array) {
      var result = [];
      if (isArrayLike(array) && array.length) {
        var shallowCopy = isString(array) ? array.split('') : baseSlice(array),
          lastElement = shallowCopy.pop(),
          pSet = powerSet(shallowCopy),
          length = pSet.length,
          index = 0;
        while (index < length) {
          var element = pSet[index];
          result.push(element);
          pSet[index] = element = baseSlice(element);
          element.push(lastElement);
          result.push(element);
          index++;
        }
      } else {
        result.push([]);
      }
      return result;
    }
